 * Hook Model: Pipeline.
 * Type: Pipeline<{ App: React.ComponentType; }, React.ComponentType>
 * Usage Example:




export default (): Plugin => ({
  setup(api) {
    const FooContext = createContext('');
    return {
      hoc({ App }, next) {
        return next({
          App: (props: any) => {
            return (



            );
